import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchTask, fetchTaskAnalytics } from '../services/api';
import BarChart from '../components/BarChart';
import LineChart from '../components/LineChart';
import AnalyticsDashboard from '../components/AnalyticsDashboard';
import DataTable from '../components/DataTable';

const TaskDetails = () => {
  const { taskId } = useParams();
  const [task, setTask] = useState(null);
  const [analytics, setAnalytics] = useState(null);
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    company: '',
    year: ''
  });
  const [showDataTable, setShowDataTable] = useState(false);
  const [compareMode, setCompareMode] = useState(false);
  const [compareFilters, setCompareFilters] = useState({
    company: '',
    year: ''
  });
  const [compareData, setCompareData] = useState(null);
  
  useEffect(() => {
    const loadTaskData = async () => {
      try {
        const taskData = await fetchTask(taskId);
        setTask(taskData);
  
        if (taskData.status === 'completed') {
          const analyticsData = await fetchTaskAnalytics(taskId);
          setAnalytics(analyticsData);
  
          if (analyticsData.raw_data) {
            setRawData(analyticsData.raw_data);
          }
        }
      } catch (err) {
        setError('Error loading task data. Please try again.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
  
    loadTaskData();
  
    let intervalId;
    if (task && task.status !== 'completed') {
      intervalId = setInterval(loadTaskData, 2000);
    }
  
    return () => {
      if (intervalId) clearInterval(intervalId);
    };
  }, [taskId, task]); // Include `task` to ensure the effect reruns when `task` changes  
  
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };
  
  const handleCompareFilterChange = (e) => {
    const { name, value } = e.target;
    setCompareFilters({
      ...compareFilters,
      [name]: value
    });
  };
  
  const applyFilters = async () => {
    setLoading(true);
    try {
      // Fetch analytics with the current filters
      const analyticsData = await fetchTaskAnalytics(taskId, filters);
      setAnalytics(analyticsData);
      
      // If compare mode is on, fetch comparison data too
      if (compareMode) {
        const compareAnalyticsData = await fetchTaskAnalytics(taskId, compareFilters);
        setCompareData(compareAnalyticsData);
      }
      
      // Assume the API returns raw data too
      if (analyticsData.raw_data) {
        setRawData(analyticsData.raw_data);
      }
    } catch (err) {
      console.error('Error applying filters:', err);
      setError('Error applying filters. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  const toggleCompareMode = () => {
    setCompareMode(!compareMode);
    if (!compareMode) {
      // Initialize compare filters with different values than main filters
      setCompareFilters({
        company: filters.company === 'Toyota' ? 'Honda' : 'Toyota',
        year: filters.year === '2023' ? '2024' : '2023'
      });
    } else {
      // Clear compare data when turning off compare mode
      setCompareData(null);
    }
  };
  
  const toggleDataTable = () => {
    setShowDataTable(!showDataTable);
  };
  
  const exportData = (format) => {
    if (!rawData || rawData.length === 0) return;
    
    const filename = `task-${taskId}-export`;
    
    if (format === 'csv') {
      // Create CSV content
      const headers = Object.keys(rawData[0]);
      const headerRow = headers.join(',');
      const dataRows = rawData.map(item => 
        headers.map(header => item[header]).join(',')
      ).join('\n');
      
      downloadFile(`${filename}.csv`, `${headerRow}\n${dataRows}`, 'text/csv');
    } else if (format === 'json') {
      downloadFile(`${filename}.json`, JSON.stringify(rawData, null, 2), 'application/json');
    }
  };
  
  const downloadFile = (filename, content, contentType) => {
    const blob = new Blob([content], { type: contentType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  
  if (loading && !task) {
    return <div className="loading-spinner"></div>;
  }
  
  if (error) {
    return <div className="error">{error}</div>;
  }
  
  if (!task) {
    return <div>Task not found</div>;
  }
  
  return (
    <div className="task-details">
      <h1>Task {taskId} Details</h1>
      
      <div className="task-info">
        <h2>Task Information</h2>
        <div className="status-indicator status-completed">
          <div className="indicator-icon"></div>
          <span>Status: {task.status}</span>
        </div>
        <table>
          <tbody>
            <tr>
              <td>Created:</td>
              <td>{new Date(task.created_at).toLocaleString()}</td>
            </tr>
            <tr>
              <td>Parameters:</td>
              <td>
                <ul>
                  <li>Start Year: {task.parameters.start_year}</li>
                  <li>End Year: {task.parameters.end_year}</li>
                  <li>
                    Companies: {task.parameters.companies && task.parameters.companies.length > 0
                      ? task.parameters.companies.join(', ')
                      : 'All'
                    }
                  </li>
                </ul>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      
      {task.status !== 'completed' ? (
        <div className="processing-message">
          <div className="loading-spinner"></div>
          <p>Task is currently {task.status}. Please wait for it to complete.</p>
        </div>
      ) : (
        <div className="visualization-section">
          <h2>Data Visualization</h2>
          
          <div className="action-buttons">
            <button onClick={toggleCompareMode} className={compareMode ? 'active' : ''}>
              {compareMode ? 'Exit Comparison Mode' : 'Enter Comparison Mode'}
            </button>
            <button onClick={toggleDataTable}>
              {showDataTable ? 'Hide Data Table' : 'Show Data Table'}
            </button>
            <div className="export-buttons">
              <span>Export:</span>
              <button onClick={() => exportData('csv')}>CSV</button>
              <button onClick={() => exportData('json')}>JSON</button>
            </div>
          </div>
          
          {analytics && !compareMode && (
            <AnalyticsDashboard data={rawData} />
          )}
          
          <div className="filters">
            <h3>Filter Data</h3>
            <div className="filter-container">
              <div className="filter-form primary-filters">
                <div className="filter-header">
                  {compareMode && <span className="filter-label">Primary Filter</span>}
                </div>
                <div className="form-group">
                  <label>Company:</label>
                  <select
                    name="company"
                    value={filters.company}
                    onChange={handleFilterChange}
                  >
                    <option value="">All Companies</option>
                    <option value="Toyota">Toyota</option>
                    <option value="Honda">Honda</option>
                    <option value="Ford">Ford</option>
                    <option value="Chevrolet">Chevrolet</option>
                    <option value="Nissan">Nissan</option>
                  </select>
                </div>
                
                <div className="form-group">
                  <label>Year:</label>
                  <select
                    name="year"
                    value={filters.year}
                    onChange={handleFilterChange}
                  >
                    <option value="">All Years</option>
                    <option value="2023">2023</option>
                    <option value="2024">2024</option>
                    <option value="2025">2025</option>
                  </select>
                </div>
              </div>
              
              {compareMode && (
                <div className="filter-form compare-filters">
                  <div className="filter-header">
                    <span className="filter-label">Comparison Filter</span>
                  </div>
                  <div className="form-group">
                    <label>Company:</label>
                    <select
                      name="company"
                      value={compareFilters.company}
                      onChange={handleCompareFilterChange}
                    >
                      <option value="">All Companies</option>
                      <option value="Toyota">Toyota</option>
                      <option value="Honda">Honda</option>
                      <option value="Ford">Ford</option>
                      <option value="Chevrolet">Chevrolet</option>
                      <option value="Nissan">Nissan</option>
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label>Year:</label>
                    <select
                      name="year"
                      value={compareFilters.year}
                      onChange={handleCompareFilterChange}
                    >
                      <option value="">All Years</option>
                      <option value="2023">2023</option>
                      <option value="2024">2024</option>
                      <option value="2025">2025</option>
                    </select>
                  </div>
                </div>
              )}
              
              <button className="apply-filters" onClick={applyFilters}>Apply Filters</button>
            </div>
          </div>
          
          {showDataTable && rawData.length > 0 && (
            <div className="data-table-section">
              <h3>Raw Data</h3>
              <DataTable data={rawData} />
            </div>
          )}
          
          {analytics && (
            <div className="charts">
              <div className="chart-container">
                <h3>Sales by Month</h3>
                <LineChart 
                  data={analytics.timeline_chart} 
                  comparisonData={compareMode ? compareData?.timeline_chart : null}
                  primaryLabel={filters.company || 'All Companies'}
                  comparisonLabel={compareFilters.company || 'All Companies'}
                />
              </div>
              
              <div className="chart-container">
                <h3>Sales by Company</h3>
                <BarChart 
                  data={analytics.bar_chart}
                  comparisonData={compareMode ? compareData?.bar_chart : null}
                  primaryLabel={filters.year || 'All Years'}
                  comparisonLabel={compareFilters.year || 'All Years'}
                />
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default TaskDetails;
